const url = $json.url ?? '';
if (url && url.length > 5) {
  return { ...$json, dedupe_key: url };
}

const raw = ($json.raw_text ?? '').toString().trim();
const trig = ($json.trigger ?? '').toString().trim();
const emo  = ($json.emotion ?? '').toString().trim();
const src  = ($json.source ?? '').toString().trim();

const dedupe_key = [src, raw, trig, emo].join('||');

return { ...$json, dedupe_key };

