function decodeHtmlAngleBrackets(str) {
  return str
    .replaceAll("&lt;&lt;", "<<")
    .replaceAll("&gt;&gt;", ">>")
    .replaceAll("&lt;", "<")
    .replaceAll("&gt;", ">");
}

function normalizePlaceholdersInString(str) {
  if (typeof str !== "string") return str;

  // 1) decode HTML escapes that n8n sometimes shows in text fields
  let s = decodeHtmlAngleBrackets(str);

